import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';
import React, { useState } from 'react';
import {Button, Spinner} from 'react-bootstrap'

function App() {

  const [two, setTwo] = useState(2)
  const [three, setThree] = useState(3)
