      }
      if (eleven !== false) {
        possibleNumbers.push(11)
      }
      if (twelve !== false && 12 >= currentValue) {
        possibleNumbers.push(12)
      }

      let combinations = []
      for (let i = 0; i < possibleNumbers.length; i++) {
        for (let j = i + 1; j < possibleNumbers.length; j++) {
          if (possibleNumbers[i] + possibleNumbers[j] === currentValue) {
            combinations.push([possibleNumbers[i], possibleNumbers[j]]);
          } 
        }
      }

      if(combinations.length === 0) {
