import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';
import React, { useState } from 'react';
import {Button, Spinner} from 'react-bootstrap'

function App() {

  const [two, setTwo] = useState(true)
  const [three, setThree] = useState(true)
  const [four, setFour] = useState(true)
  const [five, setFive] = useState(true)
  const [six, setSix] = useState(true)
  const [seven, setSeven] = useState(true)
  const [eight, setEight] = useState(true)
  const [nine, setNine] = useState(true)
  const [ten, setTen] = useState(true)
  const [eleven, setEleven] = useState(true)
  const [twelve, setTwelve] = useState(true)

  const maxDiceValue = 6
  const minDiceValue = 1

  const [dice1, setDice1] = useState(0)
  const [dice2, setDice2] = useState(0)

  const [rolling1, setRolling1] = useState(false)
  const [rolling2, setRolling2] = useState(false)

  const handleDiceRoll = (dice) => {
    setTimeout(() => {
      let result = Math.floor(Math.random() * (maxDiceValue - minDiceValue + 1)) + minDiceValue
      if (dice === 1) {
        setDice1(result)
      }
      else if (dice === 2) {
        setDice2(result)
      }
    }, 2000);
  }


  return (
    <div>
        <Button variant="secondary" size="lg" disabled={!two} onClick={() => setTwo(false)}>2</Button>
        <Button variant="secondary" size="lg" disabled={!three} onClick={() => setThree(false)}>3</Button>
        <Button variant="secondary" size="lg" disabled={!four} onClick={() => setFour(false)}>4</Button>
        <Button variant="secondary" size="lg" disabled={!five} onClick={() => setFive(false)}>5</Button>
        <Button variant="secondary" size="lg" disabled={!six} onClick={() => setSix(false)}>6</Button>
        <Button variant="secondary" size="lg" disabled={!seven} onClick={() => setSeven(false)}>7</Button>
        <Button variant="secondary" size="lg" disabled={!eight} onClick={() => setEight(false)}>8</Button>
        <Button variant="secondary" size="lg" disabled={!nine} onClick={() => setNine(false)}>9</Button>
        <Button variant="secondary" size="lg" disabled={!ten} onClick={() => setTen(false)}>10</Button>
        <Button variant="secondary" size="lg" disabled={!eleven} onClick={() => setEleven(false)}>11</Button>
        <Button variant="secondary" size="lg" disabled={!twelve} onClick={() => setTwelve(false)}>12</Button>
          <br/>
        <Button variant="success" onClick={() => handleDiceRoll(1)}>{rolling1 ? (<Spinner/>):(<>{dice1}</>)}</Button>
        <Button variant="success" onClick={() => handleDiceRoll(2)}>{dice2}</Button>
    </div>
  );
}

export default App;
